    @Test
    public void testPoistaKontrolliMerkit() {
        String test = "1 tabletti \r\ntarvittaessa \r\nja ennen nukkumaan \r\nmenoa";
        Assert.assertEquals("1 tabletti tarvittaessa ja ennen nukkumaan menoa",
                KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "1 tabletti \ntarvittaessa \rja ennen nukkumaan \r\nmenoa";
        Assert.assertEquals("1 tabletti tarvittaessa ja ennen nukkumaan menoa",
                KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "\n\r\r\n";
        Assert.assertEquals("", KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "a\n\r\r\n";
        Assert.assertEquals("a", KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "\n\ra\r\n";
        Assert.assertEquals("a", KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "\n\r\r\n\ra";
        Assert.assertEquals("a", KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "\n\r/r\r\n/n\r";
        Assert.assertEquals("/r/n", KantaCDAUtil.poistaKontrolliMerkit(test));
        test = null;
        Assert.assertNull(KantaCDAUtil.poistaKontrolliMerkit(test));
        test = "";
        Assert.assertEquals("", KantaCDAUtil.poistaKontrolliMerkit(test));

    }

    @Test
    public void testDoubleToString() {
        double test = 99999.1;
        String tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("99999.1", tulos);
        test = 99999.0;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("99999", tulos);
        test = 9999999.1234;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("9999999.1234", tulos);
        test = 9999999.0;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("9999999", tulos);
        test = 9999999.1;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("9999999.1", tulos);
        test = 99999999.0;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("99999999", tulos);
        test = 99999999.1;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("99999999.1", tulos);
        test = 9999999.112000;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("9999999.112", tulos);
        test = 9;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("9", tulos);
        test = 9.0;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("9", tulos);
        test = -9.0;
        tulos = KantaCDAUtil.doubleToString(test);
        Assert.assertEquals("-9", tulos);
    }

