    @Test
    public void testOnkoYtunnus_TunnusOnValidi() {
    	String validi_tunnus = "1234567-8";
    	Assert.assertTrue("tunnus on muodoltaan validi", KantaCDAUtil.onkoYTunnus(validi_tunnus));
    }
    
    @Test
    public void testOnkoYtunnus_TunnusEiOleValidi() {
    	String tunnus = "123456789";
    	Assert.assertFalse("tunnus ei ole muodoltaan validi", KantaCDAUtil.onkoYTunnus(tunnus));
    }
    
    @Test
    public void testOnkoYtunnus_TunnusLyhyt() {
    	String tunnus = "1234-6";
    	Assert.assertFalse("tunnus on liian lyhyt", KantaCDAUtil.onkoYTunnus(tunnus));
    }
    
    @Test
    public void testOnkoYtunnus_TunnusPitkä() {
    	String tunnus = "1234567-91";
    	Assert.assertFalse("tunnus on liian pitkä", KantaCDAUtil.onkoYTunnus(tunnus));
    }
    
    @Test
    public void testOnkoYtunnus_TunnusNull() {
    	Assert.assertFalse("tunnus on null", KantaCDAUtil.onkoYTunnus(null));
    }
    
    @Test
    public void testOnkoYtunnus_TunnusTyhja() {
    	Assert.assertFalse("tunnus on tyhjä", KantaCDAUtil.onkoYTunnus(""));
    }
